#[derive(Copy, Clone, Debug, PartialEq, Eq, PartialOrd, Ord)]
pub enum Tok {
    EOF = -1,
    AccountAddressValue = 0,
    U64Value = 1,
    NameValue = 2,
    NameBeginTyValue = 3,
    DotNameValue = 4,
    ByteArrayValue = 5,
    Exclaim = 6,
    ExclaimEqual = 7,
    Percent = 8,
    Amp = 9,
    AmpAmp = 10,
    AmpMut = 11,
    LParen = 12,
    RParen = 13,
    Star = 14,
    Plus = 15,
    Comma = 16,
    Minus = 17,
    Period = 18,
    Slash = 19,
    Colon = 20,
    Semicolon = 21,
    Less = 22,
    LessEqual = 23,
    Equal = 24,
    EqualEqual = 25,
    Greater = 26,
    GreaterEqual = 27,
    Caret = 28,
    Underscore = 29,
    Abort = 30,
    Acquires = 31,
    Address = 32,
    As = 33,
    Assert = 34,
    Bool = 35,
    BorrowGlobal = 36,
    BorrowGlobalMut = 37,
    Break = 38,
    Bytearray = 39,
    Continue = 40,
    Copy = 41,
    CreateAccount = 42,
    Else = 43,
    Exists = 44,
    False = 45,
    Freeze = 46,
    GetGasRemaining = 47,
    GetTxnGasUnitPrice = 48,
    GetTxnMaxGasUnits = 49,
    GetTxnPublicKey = 50,
    GetTxnSender = 51,
    GetTxnSequenceNumber = 52,
    If = 53,
    Import = 54,
    Let = 55,
    Loop = 56,
    Main = 57,
    Module = 58,
    Modules = 59,
    Move = 60,
    MoveFrom = 61,
    MoveToSender = 62,
    Native = 63,
    Public = 64,
    Resource = 65,
    Return = 66,
    Script = 67,
    Struct = 68,
    True = 69,
    U64 = 70,
    Unrestricted = 71,
    While = 72,
    LBrace = 73,
    Pipe = 74,
    PipePipe = 75,
    RBrace = 76,
}

impl Tok {
    fn from_usize(value: usize) -> Tok {
        match value {
            0 => Tok::AccountAddressValue,
            1 => Tok::U64Value,
            2 => Tok::NameValue,
            3 => Tok::NameBeginTyValue,
            4 => Tok::DotNameValue,
            5 => Tok::ByteArrayValue,
            6 => Tok::Exclaim,
            7 => Tok::ExclaimEqual,
            8 => Tok::Percent,
            9 => Tok::Amp,
            10 => Tok::AmpAmp,
            11 => Tok::AmpMut,
            12 => Tok::LParen,
            13 => Tok::RParen,
            14 => Tok::Star,
            15 => Tok::Plus,
            16 => Tok::Comma,
            17 => Tok::Minus,
            18 => Tok::Period,
            19 => Tok::Slash,
            20 => Tok::Colon,
            21 => Tok::Semicolon,
            22 => Tok::Less,
            23 => Tok::LessEqual,
            24 => Tok::Equal,
            25 => Tok::EqualEqual,
            26 => Tok::Greater,
            27 => Tok::GreaterEqual,
            28 => Tok::Caret,
            29 => Tok::Underscore,
            30 => Tok::Abort,
            31 => Tok::Acquires,
            32 => Tok::Address,
            33 => Tok::As,
            34 => Tok::Assert,
            35 => Tok::Bool,
            36 => Tok::BorrowGlobal,
            37 => Tok::BorrowGlobalMut,
            38 => Tok::Break,
            39 => Tok::Bytearray,
            40 => Tok::Continue,
            41 => Tok::Copy,
            42 => Tok::CreateAccount,
            43 => Tok::Else,
            44 => Tok::Exists,
            45 => Tok::False,
            46 => Tok::Freeze,
            47 => Tok::GetGasRemaining,
            48 => Tok::GetTxnGasUnitPrice,
            49 => Tok::GetTxnMaxGasUnits,
            50 => Tok::GetTxnPublicKey,
            51 => Tok::GetTxnSender,
            52 => Tok::GetTxnSequenceNumber,
            53 => Tok::If,
            54 => Tok::Import,
            55 => Tok::Let,
            56 => Tok::Loop,
            57 => Tok::Main,
            58 => Tok::Module,
            59 => Tok::Modules,
            60 => Tok::Move,
            61 => Tok::MoveFrom,
            62 => Tok::MoveToSender,
            63 => Tok::Native,
            64 => Tok::Public,
            65 => Tok::Resource,
            66 => Tok::Return,
            67 => Tok::Script,
            68 => Tok::Struct,
            69 => Tok::True,
            70 => Tok::U64,
            71 => Tok::Unrestricted,
            72 => Tok::While,
            73 => Tok::LBrace,
            74 => Tok::Pipe,
            75 => Tok::PipePipe,
            76 => Tok::RBrace,
            _ => panic!("Unknown token value: {}", value),
        }
    }
}

#[derive(Clone, Debug, PartialEq, Eq, PartialOrd, Ord)]
pub struct Token<'input>(pub Tok, pub &'input str);
impl<'a> fmt::Display for Token<'a> {
    fn fmt<'f>(&self, formatter: &mut fmt::Formatter<'f>) -> Result<(), fmt::Error> {
        fmt::Display::fmt(self.1, formatter)
    }
}

pub struct __MatcherBuilder {
    regex_vec: Vec<__regex::Regex>,
}

impl __MatcherBuilder {
    pub fn new() -> __MatcherBuilder {
        let __regex_vec = vec![
            __regex::Regex::new("^(0[Xx][0-9A-Fa-f]+)").unwrap(),
            __regex::Regex::new("^([0-9]+)").unwrap(),
            __regex::Regex::new("^([\\$A-Z_a-z][\\$0-9A-Z_a-z]*)").unwrap(),
            __regex::Regex::new("^([\\$A-Z_a-z][\\$0-9A-Z_a-z]*<)").unwrap(),
            __regex::Regex::new("^([\\$A-Z_a-z][\\$0-9A-Z_a-z]*\\.[\\$A-Z_a-z][\\$0-9A-Z_a-z]*)")
                .unwrap(),
            __regex::Regex::new("^(h\"[0-9A-Fa-f]*\")").unwrap(),
            __regex::Regex::new("^(!)").unwrap(),
            __regex::Regex::new("^(!=)").unwrap(),
            __regex::Regex::new("^(%)").unwrap(),
            __regex::Regex::new("^(\\&)").unwrap(),
            __regex::Regex::new("^(\\&\\&)").unwrap(),
            __regex::Regex::new("^(\\&mut )").unwrap(),
            __regex::Regex::new("^(\\()").unwrap(),
            __regex::Regex::new("^(\\))").unwrap(),
            __regex::Regex::new("^(\\*)").unwrap(),
            __regex::Regex::new("^(\\+)").unwrap(),
            __regex::Regex::new("^(,)").unwrap(),
            __regex::Regex::new("^(\\-)").unwrap(),
            __regex::Regex::new("^(\\.)").unwrap(),
            __regex::Regex::new("^(/)").unwrap(),
            __regex::Regex::new("^(:)").unwrap(),
            __regex::Regex::new("^(;)").unwrap(),
            __regex::Regex::new("^(<)").unwrap(),
            __regex::Regex::new("^(<=)").unwrap(),
            __regex::Regex::new("^(=)").unwrap(),
            __regex::Regex::new("^(==)").unwrap(),
            __regex::Regex::new("^(>)").unwrap(),
            __regex::Regex::new("^(>=)").unwrap(),
            __regex::Regex::new("^(\\^)").unwrap(),
            __regex::Regex::new("^(_)").unwrap(),
            __regex::Regex::new("^(abort)").unwrap(),
            __regex::Regex::new("^(acquires)").unwrap(),
            __regex::Regex::new("^(address)").unwrap(),
            __regex::Regex::new("^(as)").unwrap(),
            __regex::Regex::new("^(assert\\()").unwrap(),
            __regex::Regex::new("^(bool)").unwrap(),
            __regex::Regex::new("^(borrow_global<)").unwrap(),
            __regex::Regex::new("^(borrow_global_mut<)").unwrap(),
            __regex::Regex::new("^(break)").unwrap(),
            __regex::Regex::new("^(bytearray)").unwrap(),
            __regex::Regex::new("^(continue)").unwrap(),
            __regex::Regex::new("^(copy\\()").unwrap(),
            __regex::Regex::new("^(create_account)").unwrap(),
            __regex::Regex::new("^(else)").unwrap(),
            __regex::Regex::new("^(exists<)").unwrap(),
            __regex::Regex::new("^(false)").unwrap(),
            __regex::Regex::new("^(freeze)").unwrap(),
            __regex::Regex::new("^(get_gas_remaining)").unwrap(),
            __regex::Regex::new("^(get_txn_gas_unit_price)").unwrap(),
            __regex::Regex::new("^(get_txn_max_gas_units)").unwrap(),
            __regex::Regex::new("^(get_txn_public_key)").unwrap(),
            __regex::Regex::new("^(get_txn_sender)").unwrap(),
            __regex::Regex::new("^(get_txn_sequence_number)").unwrap(),
            __regex::Regex::new("^(if)").unwrap(),
            __regex::Regex::new("^(import)").unwrap(),
            __regex::Regex::new("^(let)").unwrap(),
            __regex::Regex::new("^(loop)").unwrap(),
            __regex::Regex::new("^(main)").unwrap(),
            __regex::Regex::new("^(module)").unwrap(),
            __regex::Regex::new("^(modules:)").unwrap(),
            __regex::Regex::new("^(move\\()").unwrap(),
            __regex::Regex::new("^(move_from<)").unwrap(),
            __regex::Regex::new("^(move_to_sender<)").unwrap(),
            __regex::Regex::new("^(native)").unwrap(),
            __regex::Regex::new("^(public)").unwrap(),
            __regex::Regex::new("^(resource)").unwrap(),
            __regex::Regex::new("^(return)").unwrap(),
            __regex::Regex::new("^(script:)").unwrap(),
            __regex::Regex::new("^(struct)").unwrap(),
            __regex::Regex::new("^(true)").unwrap(),
            __regex::Regex::new("^(u64)").unwrap(),
            __regex::Regex::new("^(unrestricted)").unwrap(),
            __regex::Regex::new("^(while)").unwrap(),
            __regex::Regex::new("^(\\{)").unwrap(),
            __regex::Regex::new("^(\\|)").unwrap(),
            __regex::Regex::new("^(\\|\\|)").unwrap(),
            __regex::Regex::new("^(\\})").unwrap(),
        ];
        __MatcherBuilder {
            regex_vec: __regex_vec,
        }
    }
    pub fn matcher<'input, 'builder>(
        &'builder self,
        s: &'input str,
    ) -> __Matcher<'input, 'builder> {
        __Matcher {
            text: s,
            consumed: 0,
            regex_vec: &self.regex_vec,
            token: (0, Token(Tok::EOF, ""), 0),
        }
    }
}

pub struct __Matcher<'input, 'builder> {
    text: &'input str,
    consumed: usize,
    regex_vec: &'builder Vec<__regex::Regex>,
    pub token: (usize, Token<'input>, usize),
}

impl<'input, 'builder> __Matcher<'input, 'builder> {
    pub fn advance(&mut self) -> Result<(), ParseError<usize, Token<'input>, failure::Error>> {
        let __text = self.text.trim_start();
        let __whitespace = self.text.len() - __text.len();
        let __start_offset = self.consumed + __whitespace;
        if __text.is_empty() {
            self.text = __text;
            self.consumed = __start_offset;
            self.token = (__start_offset, Token(Tok::EOF, ""), __start_offset);
            Ok(())
        } else {
            let mut __longest_match = 0;
            let mut __index: Option<usize> = None;
            for (__i, token_exp) in self.regex_vec.iter().enumerate() {
                if let Some(__match) = token_exp.find(__text) {
                    let __len = __match.end();
                    if __len >= __longest_match {
                        __longest_match = __len;
                        __index = Some(__i.try_into().unwrap());
                    }
                }
            }
            match __index {
                None => Err(ParseError::InvalidToken {
                    location: __start_offset,
                }),
                Some(index) => {
                    let __result = &__text[..__longest_match];
                    let __remaining = &__text[__longest_match..];
                    let __end_offset = __start_offset + __longest_match;
                    self.text = __remaining;
                    self.consumed = __end_offset;
                    self.token = (
                        __start_offset,
                        Token(Tok::from_usize(index), __result),
                        __end_offset,
                    );
                    Ok(())
                }
            }
        }
    }
}
